import chai from 'chai';
import chaiHttp from "chai-http";
import 'chai/register-should';
import app from '../index';
import {describe} from "mocha";

chai.use(chaiHttp);
const {expect} = chai;

describe('Testing the endpoints of book api', () => {
    it('should create a book', (done) => {
       const newBook = {
           title: 'First awesome book',
           price: "499",
           description: 'This is example book description'
       }

       chai.request(app)
           .post('/api/v1/books')
           .set('Accept', 'application/json')
           .send(newBook)
           .end((err, res) => {
               expect(res.status).to.equal(201);
               expect(res.body.data).to.include({
                   id: 1,
                   title: newBook.title,
                   price: newBook.price,
                   description: newBook.description,
               });
               done();
           })
    });

    it('should not create a book with incomplete parameters', (done) => {
        const newBook = {
            price: '220',
            description: "AWesome to aweome"
        };

        chai.request(app)
            .post('/api/v1/books')
            .set('Accept', 'application/json')
            .send(newBook)
            .end((err, res) => {
                expect(res.status).to.equal(400);
                done();
            })
    });

    it('It should get all books', (done) => {
        chai.request(app)
            .get('/api/v1/books')
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(200);
                res.body.data[0].should.have.property('id');
                res.body.data[0].should.have.property('title');
                res.body.data[0].should.have.property('price');
                res.body.data[0].should.have.property('description');
                done();
            });
    });

    it('It should get a particular book', (done) => {
        const bookId = 1;
        chai.request(app)
            .get(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(200);
                res.body.data.should.have.property('id');
                res.body.data.should.have.property('title');
                res.body.data.should.have.property('price');
                res.body.data.should.have.property('description');
                done();
            });
    });

    it('It should not get a particular book with invalid id', (done) => {
        const bookId = 1000;
        chai.request(app)
            .get(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(404);
                res.body.should.have.property('message').eql(`Cannot find book with the id ${bookId}`);
                done();
            });
    });

    it('It should not get a particular book with non-numeric id', (done) => {
        const bookId = 'aaa';
        chai.request(app)
            .get(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(400);
                res.body.should.have.property('message').eql('Please input a number value!');
                done();
            });
    });

    it('It should update a book', (done) => {
        const bookId = 1;
        const updatedBook = {
            id: bookId,
            title: 'Updated Awesome book',
            price: '$10.99',
            description: 'We have updated the price'
        };
        chai.request(app)
            .put(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .send(updatedBook)
            .end((err, res) => {
                expect(res.status).to.equal(200);
                expect(res.body.data.id).equal(updatedBook.id);
                expect(res.body.data.title).equal(updatedBook.title);
                expect(res.body.data.price).equal(updatedBook.price);
                expect(res.body.data.description).equal(updatedBook.description);
                done();
            });
    });

    it('It should not update a book with invalid id', (done) => {
        const bookId = '9999';
        const updatedBook = {
            id: bookId,
            title: 'Updated Awesome book again',
            price: '$11.99',
            description: 'We have updated the price'
        };
        chai.request(app)
            .put(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .send(updatedBook)
            .end((err, res) => {
                expect(res.status).to.equal(404);
                res.body.should.have.property('message').eql(`Can't find the book with the id of ${bookId}`);
                done();
            });
    });

    it('It should not update a book with non-numeric id value', (done) => {
        const bookId = 'ggg';
        const updatedBook = {
            id: bookId,
            title: 'Updated Awesome book again',
            price: '$11.99',
            description: 'We have updated the price'
        };
        chai.request(app)
            .put(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .send(updatedBook)
            .end((err, res) => {
                expect(res.status).to.equal(400);
                res.body.should.have.property('message').eql('Please input a valid number!');
                done();
            });
    });

    it('It should delete a book', (done) => {
        const bookId = 1;
        chai.request(app)
            .delete(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(200);
                expect(res.body.data).to.include({});
                done();
            });
    });

    it('It should not delete a book with invalid id', (done) => {
        const bookId = 777;
        chai.request(app)
            .delete(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(404);
                res.body.should.have.property('message').eql(`Book with the id ${bookId} can't be found!`);
                done();
            });
    });

    it('It should not delete a book with non-numeric id', (done) => {
        const bookId = 'bbb';
        chai.request(app)
            .delete(`/api/v1/books/${bookId}`)
            .set('Accept', 'application/json')
            .end((err, res) => {
                expect(res.status).to.equal(400);
                res.body.should.have.property('message').eql('Please input a number value!');
                done();
            });
    });
});

import config from "dotenv";
import express from 'express';
import cors from 'cors';
import BookRoutes from './server/routes/BookRoutes';
import UserRoutes from "./server/routes/UserRoutes";

config.config();

const app = express();

app.use('/static/uploads', express.static('static/uploads'));

app.use(express.json());
app.use(express.urlencoded({ extended: false}));

app.use((req, res,next) => {
    res.header("Access-Control-Allow-Origin", "*");
    res.header(
        "Access-Control-Allow-Headers",
        "Origin, X-Requested-With, Content-Type, Accept, Authorization"
    );
    if (req.method === "OPTIONS") {
        res.header("Access-Control-Allow-Methods", "PUT, POST, PATCH, DELETE, GET");
        return res.status(200).json({});
    }
    next();
})

app.use(cors())

const port = process.env.PORT || 8000;

app.use("/api/v1/books", BookRoutes);
app.use("/api/v1/users", UserRoutes);

app.get("*", (req, res) => {
    res.status(200).send({
        message: "Welcome asshole in this api!"
    })
});

app.listen(port, () => console.log(`Server is running on PORT ${port}`));

export default app;

import {Router} from "express";
import BookController, {resizeImage, uploadImage} from "../controllers/BookController";

const catchErrors = fn => {
    return function(req, res, next) {
        return fn(req, res, next).catch(next);
    };
};

const router = Router();

router.get("/", BookController.index);
router.post("/", uploadImage, catchErrors(resizeImage), BookController.store);
router.get("/:id", BookController.show);
router.put("/:id", BookController.update);
router.delete("/:id", BookController.destroy);

export default router;

import BookService from "../services/BookService";
import Util from "../utils/Utils";
import jimp from 'jimp';
import multer from 'multer';

const util = new Util();
const imageUploadOptions = {
    storage: multer.memoryStorage(),
    limits: {
        // storing images files up to 1mb
        fileSize: 1024 * 1024 * 1
    },
    fileFilter: (req, file, next) => {
        if (file.mimetype.startsWith("image/")) {
            next(null, true);
        } else {
            next(null, false);
        }
    }
};

exports.uploadImage = multer(imageUploadOptions).single("avatar");

exports.resizeImage = async (req, res, next) => {
    if (!req.file) {
        return next();
    }
    const extension = req.file.mimetype.split("/")[1];
    req.body.avatar = `/static/uploads/${Date.now()}.${extension}`;
    const avatar = await jimp.read(req.file.buffer);
    await avatar.resize(750, jimp.AUTO);
    await avatar.write(`./${req.body.avatar}`);
    next();
};

class BookController {
    static async index(req, res) {
        try {
            const books = await BookService.index();
            if (books.length > 0) {
                util.setSuccess(200, "Book are available and hot", books);
            } else {
                util.setSuccess(200, "No available books today.", [])
            }
            return util.send(res);
        }catch (e) {
            util.setError(400, e);
            return util.send(res);
        }
    }

    static async store(req, res) {
        console.log(req)
        if (!req.body.title ||!req.body.price ||!req.body.description) {
            util.setError(400, "Please provide or fill up all the details to complete your transaction.");
            return util.send(res);
        }
        const book = req.body;
        try {
            const createBook = await BookService.store(book);
            util.setSuccess(201, "New book information added successfully!", createBook);
            return util.send(res);
        } catch (e) {
            util.setError(400, e.message);
            return util.send(res);
        }
    }

    static async update(req, res) {
        const modify = req.body;
        const {id} = req.params;
        if (!Number(id)) {
            util.setError(400, "Please input a valid number!");
            return util.send(res);
        }

        try {
            const result = await BookService.update(id, modify);
            if (!result) {
                util.setError(404, `Can't find the book with the id of ${id}`);
            } else {
                util.setSuccess(200, "Book is updated successfully!", result);
            }
            return util.send(res);
        } catch (e) {
            util.setError(404, e)
            return util.send(res)
        }
    }

    static async show(req, res) {
        const {id} = req.params;
        if (!Number(id)) {
            util.setError(400, "Please input a number value!");
            return util.send(res);
        }
        try {
            const result = await BookService.show(id);
            if (!result) {
                util.setError(404, `Cannot find book with the id ${id}`);
            } else {
                util.setSuccess(200, "Found it!", result);
            }
            return util.send(res);
        } catch (e) {
            util.setError(404, e);
            return util.send(res)
        }
    }

    static async destroy(req, res) {
        const {id} = req.params;
        if (!Number(id)) {
            util.setError(400, "Please input a number value!");
            return util.send(res);
        }

        try {
            const result = await BookService.destroy(id);
            if (result) {
                util.setSuccess(200, "Book has been deleted!");
            } else {
                util.setError(404, `Book with the id ${id} can't be found!`);
            }
            return util.send(res);
        } catch (e) {
            util.setError(400, e);
            return util.send(res);
        }
    }
}

